  {
    "type": "color",
    "varId": "DarkPurple",
    "displayText": "Dark purple",
    "default": "rgba(63, 63, 102, 1)"
  },
  {
    "type": "color",
    "varId": "MidPurple",
    "displayText": "Med. purple",
    "default": "rgba(83, 83, 134, 1)"
  },
  {
    "type": "select",
    "varId": "customQR-Position",
    "displayText": "QR Button position",
    "default": "flex-end",
    "options": [
      {
        "label": "Left",
        "value": "flex-start"
      },
      {
        "label": "Center",
        "value": "center"
      },
      {
        "label": "Right",
        "value": "flex-end"
      }
    ]
  },
  {
    "type": "select",
    "varId": "headerFont",
    "displayText": "Header font",
    "default": "'DynaPuff', system-ui",
    "options": [
