    ]
  },
  {
    "type": "select",
    "varId": "mainFont",
    "displayText": "Main body font",
    "default": "'Roboto', sans-serif",
    "options": [
      {
        "label": "Roboto",
        "value": "'Roboto', sans-serif"
      },
      {
        "label": "Default",
        "value": "'Noto Sans', sans-serif"
      }
    ]
  },
  {
    "type": "slider",
    "varId": "borderSize",
    "displayText": "Avatar border size",
    "default": "3",
    "min": 0,
    "max": 8,
    "step": 1
  },
  {
    "type": "select",
    "varId": "avatarFrame",
    "displayText": "Avatar frame color",
    "default": "#f5c2e7, #f38ba8, #eba0ac, #fab387, #f9e2af, #a6e3a1, #94e2d5, #89dceb, #74c7ec, #89b4fa, #b4befe, #cba6f7, #f5c2e7",
    "options": [
      {
