  {
    "type": "select",
    "varId": "favoriteFrame",
    "displayText": "Favorite frame color",
    "default": "#ffd700, #ffec8b, #fff8dc, #ffd700, #daa520, #b8860b, #daa520, #ffd700, #ffec8b, #fff8dc, #ffd700",
