      {
        "label": "Rainbow",
        "value": "#f5c2e7, #f38ba8, #eba0ac, #fab387, #f9e2af, #a6e3a1, #94e2d5, #89dceb, #74c7ec, #89b4fa, #b4befe, #cba6f7, #f5c2e7"
