body {
  font-family: var(--mainFont);
  font-weight: 500;
}

/* SLIDERS */

input[type="range"]::-webkit-slider-runnable-track {
  -webkit-appearance: none;
  appearance: none;
  background: var(--SmartThemeShadowColor);
  border-radius: 0.3em;
}

input[type="range"]::-webkit-slider-thumb {
  -webkit-appearance: none;
  background: var(--SmartThemeEmColor);
  border-radius: 25%;
}

::-moz-range-track {
  height: 0.5em;
  background: var(--SmartThemeShadowColor);
  border-radius: 0.3em;
}

::-moz-range-thumb {
  background: var(--SmartThemeEmColor);
  border-radius: 25%;
  border: none;
}

::-moz-range-progress {
  background-color: var(--SmartThemeUnderlineColor);
  height: 0.5em;
  border-radius: 0.3em;
}

/* SCROLLBARS */

@-moz-document url-prefix() {
  * {
    scrollbar-width: auto;
    scrollbar-color: var(--SmartThemeUnderlineColor) transparent;
  }

  *:hover {
    scrollbar-color: var(--SmartThemeUnderlineColor) var(--SmartThemeChatTintColor);
  }
}

::-webkit-scrollbar {
  width: 0.65em;
  background-color: transparent;
}

::-webkit-scrollbar-track:hover {
  background-color: var(--SmartThemeChatTintColor);
}

::-webkit-scrollbar-thumb:vertical {
  background-color: var(--SmartThemeUnderlineColor);
}

::-webkit-scrollbar-thumb:horizontal {
  background-color: var(--SmartThemeUnderlineColor);
}

::-webkit-scrollbar-button {
  display: none;
}

/* FONT SETTINGS */

.ch_name,
h1, h2, h3, h4, h5,
.drawer-header,
.inline-drawer-header,
.characterName,
.welcomePanel .recentChatsTitle,
.standoutHeader {
  font-family: var(--headerFont);
  font-optical-sizing: auto;
  font-weight: 700;
  font-style: normal;
  font-variation-settings: "wdth" 100;
  color: var(--SmartThemeUnderlineColor);
}

.drawer-header svg,
.drawer-header .drawer-icon {
  color: var(--SmartThemeBodyColor);
  fill: var(--SmartThemeBodyColor);
}

/* AVATAR FRAME */

@property --a {
  syntax: '<angle>';
  initial-value: 0deg;
  inherits: false;
}

.avatar img {
  box-sizing: border-box;
  border: solid calc(var(--borderSize) * 1px) transparent !important;
  background: 
    conic-gradient(from var(--a),
      var(--avatarFrame)
    ) border-box;
  animation: border-spin 5s linear infinite;
  animation-play-state: var(--frameAnimation);
}
@keyframes border-spin {
  to { --a: 360deg; }
}

/* FAVORITE CHARACTER */

.character_select.is_fav .avatar img,
.group_select.is_fav .avatar img,
.group_member.is_fav .avatar img,
.avatar.is_fav img {
  background: 
    conic-gradient(from var(--a),
      var(--favoriteFrame)
    ) border-box;
  animation: border-spin 5s linear infinite;
  animation-play-state: var(--frameAnimation);
}

.character_select.is_fav .avatar,
.group_select.is_fav .avatar,
.group_member.is_fav .avatar,
.avatar.is_fav {
  outline: none;
}

/* MENU TITLE BACKGROUND */

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader{
  color: var(--SmartThemeUnderlineColor);
  background-color: var(--DarkPurple);
  background-image: 
    radial-gradient(var(--MidPurple) 2px, transparent 2px), 
    radial-gradient(var(--MidPurple) 2px, transparent 2px);
  background-size: 20px 20px;
  background-position: 0 0, 10px 10px;
}

/* TOP BAR */

#top-bar{
  box-shadow: none;
  backdrop-filter: blur(var(--SmartThemeBlurStrength));
  border-radius: 0 0 15px 15px;
  background-color: var(--TopBarBg);
}

