/* WELCOME PAGE */

.welcomeHeaderLogo{
  content: url("https://phampyk.github.io/assets/flower-divider.png");
  width: auto !important;
  height: clamp(7em, 15vw, 11em) !important;
  object-fit: contain;
  margin: 12px auto;
  display: block;
}

.welcomeHeaderVersionDisplay,
.welcomeShortcuts,
.mes.smallSysMes .mes_block {
  display: none !important;
}

/* MENU BUTTONS */

.menu_button {
  color: var(--SmartThemeBodyColor);
  filter: none;
  background-color: var(--SmartThemeChatTintColor);
  border: 1px solid var(--SmartThemeBorderColor);
  border-radius: 5px;
  padding: 3px 5px;
  width: min-content;
  cursor: pointer;
  margin: 5px 0;
  transition: var(--animation-duration-2x);
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
}

/* CODE BLOCK */

code, .hljs {
  background-color: rgba(43, 43, 70, 0.7);
  color: rgba(225, 181, 237, 0.7);
}

/* QR BAR */

#qr--bar, .qr--popout{
  background-color: var(--DarkPurple);
  background-image: 
    radial-gradient(var(--MidPurple) 2px, transparent 2px), 
    radial-gradient(var(--MidPurple) 2px, transparent 2px);
  background-size: 20px 20px;
  background-position: 0 0, 10px 10px;
}

#qr--bar > .qr--buttons{
  justify-content: var(--customQR-Position) !important;
}

#qr--bar > .qr--buttons .qr--button, #qr--popout > .qr--body > .qr--buttons .qr--button {
  color: var(--SmartThemeEmColor) !important;
  background-color: var(--SmartThemeBlurTintColor) !important;
  border: 2px solid var(--SmartThemeShadowColor) !important;
  border-radius: 5px !important;
  filter: none !important;
}

#qr--bar > .qr--buttons:hover .qr--button:hover, #qr--popout > .qr--body > .qr--buttons:hover .qr--button:hover{
  filter: brightness(120%) !important;
}

/* NOTEBOOK */

#notebookPanel .react-tabs__tab--selected {
  color: var(--DarkPurple) !important;
}

.ql-snow .ql-stroke {
  stroke: var(--SmartThemeEmColor) !important;
}

/* CSS SNIPPETS */

.csss--ok {
  background-color: var(--DarkPurple) !important;
}
